// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

contract Certificate {
    mapping(string => bytes32) public certificates;

    event CertificateIssued(string certificateId, bytes32 hash);

    function issueCertificate(string memory certificateId, bytes32 hash) public {
        certificates[certificateId] = hash;
        emit CertificateIssued(certificateId, hash);
    }

    function verifyCertificate(string memory certificateId, bytes32 hash) public view returns (bool) {
        return certificates[certificateId] == hash;
    }
}

